mapper.select:
select count(1)
from h_medical_report
where student_id = #{studentId};

select report.id as reportId, report.hospital_id as hospitalId, report.create_time, report.update_time as updateTime, mh.name as hospitalName, report.student_id as studentId from h_medical_report report left join h_medical_record record on report.medical_record_id = record.id left join m_hospital mh on report.hospital_id = mh.id where report.student_id in
#{studentId}
order by report.create_time desc

select *
from h_medical_report where id in

    #{id}
