mapper.update:
UPDATE m_screening_notice_dept_org
SET screening_task_plan_id = #{screeningTaskPlanId}, operator_id = #{operatorId}, operation_status = #{operationStatus}
WHERE screening_notice_id = #{screeningNoticeId} AND accept_org_id = #{acceptOrgId}

mapper.select:
SELECT msn.id, msn.type, msn.title, msn.content, msn.start_time, msn.end_time, msn.release_time, msn.screening_task_id, msn.operator_id,msn.screening_type, msn.create_time, msn.release_status, msn.create_user_id, msn.gov_dept_id, msndo.id as screening_notice_dept_org_id, msndo.district_id, msndo.accept_org_id, msndo.screening_task_plan_id, msndo.operation_status, msndo.create_time as accept_time FROM m_screening_notice_dept_org msndo LEFT JOIN m_screening_notice msn ON msndo.screening_notice_id = msn.id
and msn.`type` = #{param.type} and msndo.`accept_org_id` = #{param.govDeptId}
and msn.`title` like CONCAT('%',#{param.titleLike},'%')
and DATE(msn.`start_time`) < DATE(#{param.endCreateTime}) and DATE(msn.`end_time`) >= DATE(#{param.startCreateTime})
and msn.screening_type = #{param.screeningType}
and msn.create_user_id in
#{createUserId}
order by msn.create_time desc

select

from m_screening_notice_dept_org
where screening_notice_id = #{noticeId}
  and accept_org_id = #{orgId}
limit 1
