mapper.select:
SELECT mspss.student_name, mspss.gender, mspss.school_id, mspss.grade_name, mspss.class_name, mvsr.vision_data, mvsr.computer_optometry, msc.school_grade_code, msc.glasses_type, msc.naked_vision_warning_level, msc.screening_plan_school_student_id, msc.vision_correction FROM m_stat_conclusion msc LEFT JOIN m_vision_screening_result mvsr ON msc.result_id = mvsr.id LEFT JOIN m_screening_plan_school_student mspss ON msc.screening_plan_school_student_id = mspss.id WHERE msc.is_valid = 1 AND msc.is_rescreen = 0
and msc.src_screening_notice_id = #{screeningNoticeId}
and msc.plan_id = #{planId} AND msc.school_id = #{schoolId} ORDER BY mspss.school_name, mspss.grade_name, mspss.class_name, mspss.student_no

select distinct school_id from m_stat_conclusion
WHERE src_screening_notice_id = #{screeningNoticeId}
AND district_id IN
#{id}

select plan_id, school_id from m_stat_conclusion where DATE(create_time) = DATE(#{date})
and is_rescreen = #{isRescreen}
group by plan_id, school_id
