mapper.select:
SELECT school_id
FROM m_vision_screening_result
WHERE task_id = #{taskId}
  and screening_org_id = #{orgId}
GROUP BY school_id

SELECT create_user_id
FROM m_vision_screening_result
WHERE plan_id = #{planId}
  and screening_org_id = #{orgId}
GROUP BY create_user_id

select

from m_vision_screening_result
where school_id = #{schoolId}
  and screening_org_id = #{orgId}
  and plan_id = #{planId}
  and is_double_screen = 0

select

from m_vision_screening_result where screening_plan_school_student_id in

    #{planStudentId}

select
from m_vision_screening_result where plan_id = #{planId} and student_id in
#{studentId}
