mapper.update:
UPDATE m_screening_notice_dept_org
SET operator_id = #{operatorId}, operation_status = #{operationStatus}
WHERE screening_notice_id = #{screeningNoticeId} AND accept_org_id = #{acceptOrgId}

mapper.select:
SELECT
  msndo.id, msn.type, msn.title, msn.content, msn.start_time, msn.end_time, msn.release_time, msn.screening_task_id,
  msndo.district_id, md.name as district_name, msndo.accept_org_id, mgd.name as gov_dept_name, msndo.operation_status
FROM
m_screening_notice_dept_org msndo
LEFT JOIN m_screening_notice msn ON msndo.screening_notice_id = msn.id
LEFT JOIN m_district md ON msndo.district_id = md.id
LEFT JOIN m_government_department mgd ON msndo.accept_org_id = mgd.id


        and msn.`release_status` = #{param.releaseStatus}
        and msn.`type` = #{param.type}
        and msndo.operation_status = #{param.operationStatus}
        and msndo.`district_id` = #{param.districtId}
        and msndo.`accept_org_id` = #{param.govDeptId}

            and msn.`title` like CONCAT('%',#{param.titleLike},'%')


            and  DATE(msn.`start_time`) <= DATE(#{param.endCreateTime}) and  DATE(msn.`end_time`) >= DATE(#{param.startCreateTime})
