mapper.select:
SELECT s.class_id AS clazzId, s.student_name,s.class_name AS clazzName, s.school_name, s.school_id, s.grade_id
, s.grade_name, s.student_id, s.student_age, s.screening_plan_id, s.gender
, s.screening_task_id, r.biometric_data,r.update_time, r.computer_optometry, r.is_double_screen, r.vision_data
, r.other_eye_diseases
FROM m_screening_plan_school_student s
LEFT JOIN m_vision_screening_result r ON s.id = r.screening_plan_school_student_id
WHERE
s.school_id = #{data.schoolId}

    AND s.class_name = #{data.clazzName}


    AND s.grade_name = #{data.gradeName}


    AND s.screening_org_id = #{data.depId}

SELECT mspss.student_id as id, mspss.student_no as sno, mspss.student_name as name, mspss.gender, mspss.birthday, mspss.province_code, mspss.city_code, mspss.area_code, mspss.town_code, mspss.address, mspss.id_card, mspss.nation, mspss.parent_phone, msg.name as grade_name, msc.name as class_name FROM m_screening_plan_school_student mspss
LEFT JOIN m_school_grade msg ON msg.id = mspss.grade_id LEFT JOIN m_school_class msc ON msc.id = mspss.class_id
AND mspss.screening_plan_id = #{param.screeningPlanId} AND mspss.school_id = #{param.schoolId}
and mspss.`student_name` like CONCAT('%',#{param.nameLike},'%')
and mspss.`id_card` like CONCAT('%',#{param.idCardLike},'%')
and mspss.`student_no` like CONCAT('%',#{param.snoLike},'%')
and mspss.`parent_phone` like CONCAT('%',#{param.phoneLike},'%')
and mspss.`gender` = #{param.gender}
and mspss.`grade_id` in #{item}

select *
from m_screening_plan_school_student
where screening_plan_id = #{screeningPlanId} and school_id = #{schoolId}
and id_card in #{item}
